    private void bttn00ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttn00ActionPerformed

        if (vez.equals("X")) {
            bttn00.setText("X");
            vez = "O";
        } else if (vez.equals("O")) {
            bttn00.setText("O");
            vez = "X";
        }
        lbVez.setText(vez);

    }//GEN-LAST:event_bttn00ActionPerformed

