    public int contadorX = 0;
    public int contadorO = 0;
    private void bttnVerificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttnVerificarActionPerformed
        if (bttn00.getText() == bttn11.getText()) { //Diagonal Principal
            if (bttn11.getText() == bttn22.getText()) {
                if (bttn00.getText().equals("X")) {
                    contadorX++;
                } else if (bttn00.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn02.getText() == bttn11.getText()) { //Diagonal Secundária
            if (bttn11.getText() == bttn20.getText()) {
                if (bttn02.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn02.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn00.getText() == bttn01.getText()) { //3 de cima
            if (bttn00.getText() == bttn02.getText()) {
                if (bttn00.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn00.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn10.getText() == bttn11.getText()) { //3 do meio
            if (bttn10.getText() == bttn12.getText()) {
                if (bttn10.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn10.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn20.getText() == bttn21.getText()) { //3 de baixo
            if (bttn20.getText() == bttn22.getText()) {
                if (bttn20.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn20.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn00.getText() == bttn10.getText()) { //3 da esquerda
            if (bttn10.getText() == bttn20.getText()) {
                if (bttn00.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn00.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn01.getText() == bttn11.getText()) { //3 do meio
            if (bttn01.getText() == bttn21.getText()) {
                if (bttn01.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn01.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        if (bttn02.getText() == bttn12.getText()) { //3 da direita
            if (bttn12.getText() == bttn22.getText()) {
                if (bttn02.getText().equals("X")) {
                    contadorX++;
                }
                if (bttn02.getText().equals("O")) {
                    contadorO++;
                }
            }
        }
        String contadorOO = String.valueOf(contadorO);
        lbO.setText(contadorOO);
        
        String contadorXX = String.valueOf(contadorX);
        lbX.setText(contadorXX);
    


    }//GEN-LAST:event_bttnVerificarActionPerformed

